css/style.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --bg-primary: #f5f5f5;
    --bg-secondary: #ffffff;
    --text-primary: #5a4a5a;
    --text-secondary: #9a7a9a;
    --accent: #ff69b4;
    --accent-light: #ffb3d9;
    --shadow: rgba(0, 0, 0, 0.08);
    --border: #e0e0e0;
    --card-bg: rgba(255, 255, 255, 0.6);
    --pink: #ff69b4;
    --dark-pink: #ff1493;
    --purple: #da70d6;
}

body.dark-mode {
    --bg-primary: #1a1a1a;
    --bg-secondary: #2d2d2d;
    --text-primary: #e0e0e0;
    --text-secondary: #a0a0a0;
    --accent: #ff69b4;
    --accent-light: #ff8fc7;
    --shadow: rgba(0, 0, 0, 0.4);
    --border: #404040;
}

body {
    font-family: 'Poppins', sans-serif;
    background: linear-gradient(135deg, var(--bg-primary) 0%, #ffd6e8 100%);
    color: var(--text-primary);
    min-height: 100vh;
    transition: background 0.3s ease, color 0.3s ease;
}

body.dark-mode {
    background: linear-gradient(135deg, #1a1a1a 0%, #2d1f3d 100%);
}

.hearts-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
    overflow: hidden;
}

.heart-float {
    position: absolute;
    color: var(--accent);
    font-size: 16px;
    animation: float 8s ease-in-out infinite;
    opacity: 0.4;
}

@keyframes float {
    0% { transform: translateY(100vh); opacity: 0; }
    10% { opacity: 0.4; }
    90% { opacity: 0.4; }
    100% { transform: translateY(-100px); opacity: 0; }
}

.dark-mode-toggle {
    position: fixed;
    top: 20px;
    right: 20px;
    z-index: 101;
    background: rgba(255, 255, 255, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    width: 48px;
    height: 48px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.4rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 20px rgba(31, 38, 135, 0.1);
}

body.dark-mode .dark-mode-toggle {
    background: rgba(45, 45, 45, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.dark-mode-toggle:hover {
    transform: scale(1.1) rotate(20deg);
    box-shadow: 0 6px 30px rgba(255, 105, 180, 0.3);
}

.nav {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    z-index: 100;
    padding: 1rem 0;
    border-bottom: 1px solid rgba(255, 105, 180, 0.2);
    transition: all 0.3s ease;
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.05);
}

body.dark-mode .nav {
    background: rgba(45, 45, 45, 0.7);
    border-bottom: 1px solid rgba(255, 105, 180, 0.3);
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    padding: 0 1rem;
    position: relative;
}

.hamburger {
    display: none;
    flex-direction: column;
    gap: 5px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0.5rem;
    z-index: 102;
}

.hamburger span {
    width: 25px;
    height: 3px;
    background: var(--accent);
    border-radius: 3px;
    transition: all 0.3s ease;
}

.hamburger.active span:nth-child(1) {
    transform: rotate(45deg) translate(8px, 8px);
}

.hamburger.active span:nth-child(2) {
    opacity: 0;
}

.hamburger.active span:nth-child(3) {
    transform: rotate(-45deg) translate(7px, -7px);
}

.nav-links {
    display: flex;
    gap: 0.5rem;
    flex-wrap: wrap;
    justify-content: center;
}

.nav-link {
    text-decoration: none;
    color: var(--text-secondary);
    font-weight: 500;
    padding: 0.6rem 1rem;
    border-radius: 12px;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.3rem;
    white-space: nowrap;
    font-size: 0.95rem;
}

.nav-link:hover {
    color: var(--text-primary);
    background: rgba(255, 255, 255, 0.5);
    backdrop-filter: blur(10px);
    transform: translateY(-2px);
}

body.dark-mode .nav-link:hover {
    background: rgba(255, 255, 255, 0.1);
}

.nav-link.active {
    background: var(--accent);
    color: white;
    box-shadow: 0 4px 12px rgba(255, 105, 180, 0.4);
}

.nav-link.secret {
    opacity: 0.6;
    font-style: italic;
}

.share-btn {
    position: fixed;
    bottom: 30px;
    right: 30px;
    z-index: 100;
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    color: var(--accent);
    padding: 0.9rem 1.3rem;
    border-radius: 16px;
    border: 1px solid rgba(255, 105, 180, 0.3);
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 1rem;
    font-weight: 500;
    box-shadow: 0 4px 16px rgba(255, 105, 180, 0.2);
    transition: all 0.3s ease;
}

body.dark-mode .share-btn {
    background: rgba(45, 45, 45, 0.7);
    border: 1px solid rgba(255, 105, 180, 0.2);
}

.share-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 24px rgba(255, 105, 180, 0.35);
    border-color: var(--accent);
    background: rgba(255, 255, 255, 0.85);
}

body.dark-mode .share-btn:hover {
    background: rgba(45, 45, 45, 0.85);
}

.share-btn::before {
    content: '🔗';
    font-size: 1.1rem;
}

@keyframes pulse-glow {
    0%, 100% { 
        box-shadow: 0 4px 16px rgba(255, 105, 180, 0.2);
    }
    50% { 
        box-shadow: 0 4px 16px rgba(255, 105, 180, 0.35);
    }
}

.share-menu-container {
    position: relative;
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
}

.share-menu-btn {
    background: rgba(255, 105, 180, 0.2);
    backdrop-filter: blur(10px);
    color: var(--accent);
    border: 1px solid rgba(255, 105, 180, 0.3);
    padding: 0.6rem 1rem;
    border-radius: 12px;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.95rem;
    font-weight: 500;
    transition: all 0.3s ease;
}

.share-menu-btn:hover {
    background: rgba(255, 105, 180, 0.3);
    transform: translateY(-2px);
}

.share-menu-dropdown {
    position: absolute;
    top: calc(100% + 0.5rem);
    right: 0;
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(30px);
    -webkit-backdrop-filter: blur(30px);
    border-radius: 16px;
    padding: 0.5rem;
    box-shadow: 0 8px 32px rgba(31, 38, 135, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.3);
    min-width: 200px;
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all 0.3s ease;
    z-index: 1000;
}

body.dark-mode .share-menu-dropdown {
    background: rgba(45, 45, 45, 0.95);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.share-menu-container.active .share-menu-dropdown {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
}

.share-menu-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem 1rem;
    color: var(--text-primary);
    text-decoration: none;
    border-radius: 10px;
    transition: all 0.2s ease;
    cursor: pointer;
    background: none;
    border: none;
    width: 100%;
    text-align: left;
    font-size: 0.95rem;
}

.share-menu-item:hover {
    background: rgba(255, 105, 180, 0.15);
    color: var(--accent);
}

.share-menu-item .share-icon {
    font-size: 1.2rem;
    width: 24px;
    text-align: center;
}

@keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

.sparkle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: var(--accent);
    border-radius: 50%;
    animation: sparkle-float 3s ease-in-out infinite;
    pointer-events: none;
}

@keyframes sparkle-float {
    0%, 100% { 
        opacity: 0;
        transform: translateY(0) scale(0);
    }
    50% { 
        opacity: 1;
        transform: translateY(-30px) scale(1);
    }
}

.main {
    padding-top: 80px;
    min-height: 100vh;
    position: relative;
    z-index: 2;
}

.container {
    max-width: 1000px;
    margin: 0 auto;
    padding: 2rem 1rem;
    text-align: center;
}

.hero {
    margin-bottom: 4rem;
    padding: 3rem 0;
}

.hero-content {
    max-width: 700px;
    margin: 0 auto;
}

.hero-title {
    font-family: 'Kalam', cursive;
    font-size: 4rem;
    color: var(--accent);
    margin-bottom: 1rem;
    animation: fadeInDown 0.8s ease-out;
}

.hero-subtitle {
    font-size: 1.5rem;
    color: var(--text-secondary);
    font-weight: 400;
    margin-bottom: 3rem;
    animation: fadeInUp 0.8s ease-out 0.2s both;
}

.hero-counter {
    background: rgba(255, 255, 255, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-radius: 24px;
    padding: 2.5rem 2rem;
    box-shadow: 0 8px 32px rgba(31, 38, 135, 0.15);
    border: 1px solid rgba(255, 255, 255, 0.3);
    transition: all 0.3s ease;
    animation: fadeInUp 0.8s ease-out 0.4s both;
}

body.dark-mode .hero-counter {
    background: rgba(45, 45, 45, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.hero-counter:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 12px 40px rgba(255, 105, 180, 0.3);
}

.counter-wrapper {
    margin-bottom: 1rem;
}

.counter-number {
    font-family: 'Kalam', cursive;
    font-size: 4.5rem;
    color: var(--accent);
    font-weight: 700;
    margin-bottom: 0.5rem;
    line-height: 1;
}

.counter-label {
    font-size: 1.3rem;
    color: var(--text-primary);
    font-weight: 600;
    letter-spacing: 1px;
    text-transform: uppercase;
}

.counter-date {
    font-size: 1rem;
    color: var(--text-secondary);
    font-style: italic;
}

@keyframes fadeInDown {
    from {
        opacity: 0;
        transform: translateY(-30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.greeting {
    margin-bottom: 3rem;
}

.title {
    font-family: 'Kalam', cursive;
    font-size: 3rem;
    color: var(--accent);
    margin-bottom: 0.5rem;
}

.subtitle {
    font-size: 1.2rem;
    color: var(--text-secondary);
    font-weight: 400;
}

.days-section {
    margin-bottom: 3rem;
}

.days-card {
    background: rgba(255, 255, 255, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-radius: 24px;
    padding: 2rem;
    box-shadow: 0 8px 32px rgba(31, 38, 135, 0.15);
    border: 1px solid rgba(255, 255, 255, 0.3);
    transition: all 0.3s ease;
}

body.dark-mode .days-card {
    background: rgba(45, 45, 45, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.days-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 12px 40px rgba(255, 105, 180, 0.3);
}

.days-number {
    font-family: 'Kalam', cursive;
    font-size: 3.5rem;
    color: var(--accent);
    font-weight: 700;
    margin-bottom: 0.5rem;
}

.days-text {
    font-size: 1.1rem;
    color: var(--text-primary);
    margin-bottom: 0.3rem;
    font-weight: 500;
}

.days-date {
    font-size: 0.9rem;
    color: var(--text-secondary);
}

.love-section {
    margin-bottom: 3rem;
}

.love-btn {
    background: var(--accent);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 12px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin: 0 auto 1rem;
}

.love-btn:hover {
    background: var(--accent-light);
    transform: translateY(-2px);
}

.love-btn:active {
    transform: translateY(0);
}

.heart {
    animation: heartbeat 1.5s ease-in-out infinite;
}

@keyframes heartbeat {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
}

.love-count {
    font-size: 1.3rem;
    color: var(--accent);
    font-weight: 600;
    margin-bottom: 0.5rem;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.love-count.show {
    opacity: 1;
}

.milestone {
    font-family: 'Kalam', cursive;
    font-size: 1.2rem;
    color: var(--text-primary);
    margin-top: 0.5rem;
    opacity: 0;
    transition: all 0.3s ease;
}

.milestone.show {
    opacity: 1;
}

.actions {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1.5rem;
    margin-bottom: 3rem;
}

.featured-section {
    margin: 3rem 0;
}

.section-title {
    text-align: center;
    font-family: 'Kalam', cursive;
    font-size: 1.8rem;
    color: var(--accent);
    margin-bottom: 2rem;
}

.featured-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1.5rem;
}

.featured-card {
    background: rgba(255, 255, 255, 0.5);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-radius: 20px;
    padding: 2rem 1.5rem;
    text-align: center;
    text-decoration: none;
    color: var(--text-primary);
    border: 1px solid rgba(255, 255, 255, 0.3);
    transition: all 0.3s ease;
}

body.dark-mode .featured-card {
    background: rgba(45, 45, 45, 0.5);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.featured-card:hover {
    transform: translateY(-8px) scale(1.03);
    box-shadow: 0 12px 40px rgba(255, 105, 180, 0.3);
}

.featured-emoji {
    font-size: 3rem;
    margin-bottom: 1rem;
}

.featured-title {
    font-weight: 600;
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
    color: var(--text-primary);
}

.featured-desc {
    font-size: 0.9rem;
    color: var(--text-secondary);
}

.action-btn {
    background: rgba(255, 255, 255, 0.5);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    color: var(--text-primary);
    text-decoration: none;
    padding: 1.5rem;
    border-radius: 20px;
    box-shadow: 0 8px 32px rgba(31, 38, 135, 0.1);
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
    font-weight: 500;
    border: 1px solid rgba(255, 255, 255, 0.3);
}

body.dark-mode .action-btn {
    background: rgba(45, 45, 45, 0.5);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.action-btn:hover {
    transform: translateY(-6px) scale(1.05);
    box-shadow: 0 12px 40px rgba(255, 105, 180, 0.3);
    border-color: var(--accent);
    background: rgba(255, 255, 255, 0.7);
}

body.dark-mode .action-btn:hover {
    background: rgba(45, 45, 45, 0.7);
}

.btn-icon {
    font-size: 2rem;
}

.footer {
    background: var(--bg-secondary);
    text-align: center;
    padding: 2rem;
    color: var(--text-secondary);
    border-top: 1px solid var(--border);
    margin-top: 4rem;
    transition: all 0.3s ease;
}

@media (max-width: 900px) {
    .hamburger {
        display: flex;
    }

    .nav-links {
        position: fixed;
        top: 70px;
        left: 0;
        right: 0;
        background: rgba(255, 255, 255, 0.98);
        backdrop-filter: blur(30px);
        -webkit-backdrop-filter: blur(30px);
        flex-direction: column;
        padding: 1.5rem;
        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
        border-bottom: 1px solid rgba(255, 105, 180, 0.2);
        max-height: 0;
        overflow: hidden;
        opacity: 0;
        transition: all 0.3s ease;
    }

    body.dark-mode .nav-links {
        background: rgba(45, 45, 45, 0.98);
        border-bottom: 1px solid rgba(255, 105, 180, 0.3);
    }

    .nav-links.active {
        max-height: 500px;
        opacity: 1;
    }

    .nav-link {
        width: 100%;
        justify-content: center;
        padding: 1rem;
        font-size: 1rem;
    }
}

@media (max-width: 768px) {
    .hero-title {
        font-size: 3rem;
    }

    .hero-subtitle {
        font-size: 1.2rem;
    }

    .counter-number {
        font-size: 3.5rem;
    }

    .counter-label {
        font-size: 1.1rem;
    }

    .title {
        font-size: 2.2rem;
    }

    .subtitle {
        font-size: 1rem;
    }

    .days-number {
        font-size: 2.5rem;
    }

    .featured-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .love-btn {
        font-size: 1rem;
        padding: 0.9rem 1.8rem;
    }

    .actions {
        grid-template-columns: 1fr;
    }

    .dark-mode-toggle {
        width: 40px;
        height: 40px;
        font-size: 1.2rem;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 1.5rem 1rem;
    }

    .hero-title {
        font-size: 2.2rem;
    }

    .hero-subtitle {
        font-size: 1rem;
    }

    .hero-counter {
        padding: 2rem 1.5rem;
    }

    .counter-number {
        font-size: 2.5rem;
    }

    .counter-label {
        font-size: 1rem;
    }

    .title {
        font-size: 1.8rem;
    }

    .days-card {
        padding: 1.5rem;
    }

    .days-number {
        font-size: 2rem;
    }

    .featured-grid {
        grid-template-columns: 1fr;
    }
}

